mapper.select:
select *
from board
where delete_yn!=1 and bno=#{bno}

SELECT *
FROM file
WHERE delete_yn!=1 and bno=#{bno}

mapper.update:
UPDATE board
SET title=#{title}, content=#{content},regdate=NOW(), hit = #{hit}, delete_yn=0, user_idx = #{userIdx}
WHERE bno = #{bno}

update board
SET delete_yn=1
WHERE bno=#{bno}
