mapper.select:
select *
from board
where delete_yn!=1 and bno=#{bno}

mapper.update:
update board
SET hit= hit+1
WHERE bno=#{bno}

mapper.select:
SELECT count(*)
FROM board
WHERE delete_yn!=1 AND board_type=#{boardType}

SELECT *
FROM file
WHERE delete_yn!=1 and bno=#{bno}

mapper.update:
UPDATE board
SET title=#{title}, content=#{content}, writer=#{writer}, regdate=NOW(), delete_yn=0, board_type=#{boardType}
WHERE bno = #{bno}

update board
SET delete_yn=1
WHERE bno=#{bno}
